#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="E:\reasonBlog\reasonServer\node_modules\.pnpm\express-generator@4.16.1\node_modules\express-generator\bin\node_modules;E:\reasonBlog\reasonServer\node_modules\.pnpm\express-generator@4.16.1\node_modules\express-generator\node_modules;E:\reasonBlog\reasonServer\node_modules\.pnpm\express-generator@4.16.1\node_modules;E:\reasonBlog\reasonServer\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/e/reasonBlog/reasonServer/node_modules/.pnpm/express-generator@4.16.1/node_modules/express-generator/bin/node_modules:/mnt/e/reasonBlog/reasonServer/node_modules/.pnpm/express-generator@4.16.1/node_modules/express-generator/node_modules:/mnt/e/reasonBlog/reasonServer/node_modules/.pnpm/express-generator@4.16.1/node_modules:/mnt/e/reasonBlog/reasonServer/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../bin/express-cli.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../bin/express-cli.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../bin/express-cli.js" $args
  } else {
    & "node$exe"  "$basedir/../../bin/express-cli.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
